import ReactDOM from 'react-dom';
import React from 'react';
import { createClient } from '@supabase/supabase-js';
import { SessionContextProvider, useSessionContext, useSession, useSupabaseClient } from '@supabase/auth-helpers-react';
import DateTimePicker from 'react-datetime-picker';
import {useState} from 'react';
// import './calendar.css';


function Calendar(){
    const session = useSession();
    const supabase = useSupabaseClient();
    const { isLoading } = useSessionContext();
    const [start, setStart ] = useState(new Date());
    const [end, setEnd ] = useState(new Date());
    const [event, setEventName ] = useState("");
    const [eventDescription, setEventDescription ] = useState("");


    if(isLoading) {
        return <></>
    }

    async function googleSignIn(){
        const { error } = await supabase.auth.signInWithOAuth({
            provider: 'google',
            options: {
                scopes: ['https://www.googleapis.com/auth/calendar'],
                redirectURI: 'https://localhost:3000/researcher-calendar'
            }
        });
        if(error) {
            alert("Error logging into Google provider with Supabase", error)
            console.log(error);
        }
    }

    async function signOut(){
        await supabase.auth.signOut();
    }
    async function createCalendarEvent() {
        console.log("Creating calendar event");
        const event = {
          'summary': eventName,
          'description': eventDescription,
          'start': {
            'dateTime': start.toISOString(), // Date.toISOString() ->
            'timeZone': Intl.DateTimeFormat().resolvedOptions().timeZone // America/Los_Angeles
          },
          'end': {
            'dateTime': end.toISOString(), // Date.toISOString() ->
            'timeZone': Intl.DateTimeFormat().resolvedOptions().timeZone // America/Los_Angeles
          }
        }
        await fetch("https://www.googleapis.com/calendar/v3/calendars/primary/events", {
            method: "POST",
            headers: {
              'Authorization':'Bearer ' + session.provider_token // Access token for google
            },
            body: JSON.stringify(event)
          }).then((data) => {
            return data.json();
          }).then((data) => {
            console.log(data);
            alert("Event created, check your Google Calendar!");
          });
        }


    console.log(session);
    console.log(start);

    return(
    <div className="App">
      <div style={{width: "400px", margin: "30px auto"}}>
        {session ?
          <>
            <h2>Hey there {session.user.email}</h2>
            <p>Start of your event</p>
            <DateTimePicker onChange={setStart} value={start} />
            <p>End of your event</p>
            <DateTimePicker onChange={setEnd} value={end} />
            <p>Event name</p>
            <input type="text" onChange={(e) => setEventName(e.target.value)} />
            <p>Event description</p>
            <input type="text" onChange={(e) => setEventDescription(e.target.value)} />
            <hr />
            <button onClick={() => createCalendarEvent()}>Create Calendar Event</button>
            <p></p>
            <button onClick={() => signOut()}>Sign Out</button>
          </>
          :
          <>
            <button onClick={() => googleSignIn()}>Sign In With Google</button>
          </>
        }
      </div>
    </div>

    );
}

export default Calendar;
