import React, { useState } from 'react';
import { Modal, Button, Card, Row, Col, Dropdown } from 'react-bootstrap';

const Widget = ({ data, onClick }) => {
  return (
    <Col sm={4}>
      <Card style={{ margin: '10px' }}>
        <Card.Body>
          <Card.Title>{data.title}</Card.Title>
          <Card.Text>{data.department}</Card.Text>
          <Button variant="primary" onClick={() => onClick(data)}>
            View Details
          </Button>
        </Card.Body>
      </Card>
    </Col>
  );
};

const MyVerticallyCenteredModal = ({ data, show, onHide }) => {
  const handleFavorite = () => {
    // Handle favorite logic
  };

  const handleSignup = () => {
    // Handle signup logic
  };

  return (
    <Modal show={show} onHide={onHide} size="lg" centered>
      <Modal.Header closeButton>
        <Modal.Title>{data.title}</Modal.Title>
      </Modal.Header>
      <Modal.Body>
        {/* Modal body content */}
      </Modal.Body>
      <Modal.Footer>
        <Button variant="secondary" onClick={handleFavorite}>
          Favorite
        </Button>
        <Button variant="secondary" onClick={handleSignup} style={{ backgroundColor: '#4169E1' }}>
          Signup
        </Button>
      </Modal.Footer>
    </Modal>
  );
};

const WidgetGrid = ({ serverData, selectedAge, selectedSex }) => {
  const [modalShow, setModalShow] = useState(false);
  const [selectedData, setSelectedData] = useState(null);

  const handleWidgetClick = (data) => {
    setSelectedData(data);
    setModalShow(true);
  };

  // Filtering logic based on selectedAge and selectedSex

  return (
    <div>
        <Row>
          {widgetsToRender.map((item) => (
            <Widget key={item.id} data={item} onClick={handleWidgetClick} />
          ))}
        </Row>

      {selectedData && (
        <MyVerticallyCenteredModal
          show={modalShow}
          onHide={() => setModalShow(false)}
          data={selectedData}
        />
      )}
    </div>
  );
};

function ParSearch() {
  const [serverData, setServerData] = useState([]);
  const [selectedAge, setSelectedAge] = useState('All Ages');
  const [selectedSex, setSelectedSex] = useState('All');

  const handleAgeSelect = (eventKey) => {
    setSelectedAge(eventKey);
  };

  const handleSexSelect = (eventKey) => {
    setSelectedSex(eventKey);
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    // Handle form submission and fetch data
  };

  return (
    <div>
      <div style={{ background: '#4169E1', color: 'white' }}>
        <h1 className="title" style={{ fontSize: '2em', margin: '0', paddingTop: '30px', textAlign: 'center' }}>
          Search for Studies
        </h1>
        <form action="/participant-search" method="POST" onSubmit={handleSubmit}>
          <div className="form-container" style={{ display: 'flex', alignItems: 'center', justifyContent: 'center' }}>
            {/* Search form elements */}
          </div>
        </form>
        <WidgetGrid serverData={serverData} selectedAge={selectedAge} selectedSex={selectedSex} />
      </div>
    </div>
  );
}

export default ParSearch;
