import React, { useState, useEffect } from 'react';
import { Modal, Button, Card, Row, Col, Dropdown } from 'react-bootstrap';
// import React from 'react';
import '../../../App.css';
import axios from 'axios';
import '../../HeroSection.css';
import { useNavigate } from 'react-router-dom';
import '../RegisterInfo.css';
import 'bootstrap/dist/css/bootstrap.min.css';
import 'bootstrap/dist/js/bootstrap.bundle.min.js';
// import Dropdown from 'react-bootstrap/Dropdown';
// import React, { useState, useEffect } from 'react';
import { useHistory } from 'react-router-dom';
// import { Modal, Button, Card, Row, Col } from 'react-bootstrap'


const generateFakeData = () => {
  const fakeData = [];

  for (let i = 1; i <= 10; i++) {
    fakeData.push({
      id: i,
      title: `Study Title ${i}`,
      department: `Department ${i}`,
      // Add more properties as needed
    });
  }

  return fakeData;
};

const Widget = ({ data, onClick }) => {
  return (
    <Col sm={4}>
      <Card style={{ margin: '10px' }}>
        <Card.Body>
          <Card.Title>{data.title}</Card.Title>
          <Card.Text>{data.department}</Card.Text>
          <Button variant="primary" onClick={() => onClick(data)}>
            View Details
          </Button>
        </Card.Body>
      </Card>
    </Col>
  );
};

const MyVerticallyCenteredModal = ({ data, show, onHide }) => {
  const handleFavorite = () => {
    // Handle favorite logic
  };

  const handleSignup = () => {
    // Handle signup logic
  };

  return (
    <Modal show={show} onHide={onHide} size="lg" centered>
      <Modal.Header closeButton>
        <Modal.Title>{data.title}</Modal.Title>
      </Modal.Header>
      <Modal.Body>
        {/* Modal body content */}
      </Modal.Body>
      <Modal.Footer>
        <Button variant="secondary" onClick={handleFavorite}>
          Favorite
        </Button>
        <Button variant="secondary" onClick={handleSignup} style={{ backgroundColor: '#4169E1' }}>
          Signup
        </Button>
      </Modal.Footer>
    </Modal>
  );
};

const WidgetGrid = ({ serverData, selectedAge, selectedSex }) => {
  const [modalShow, setModalShow] = useState(false);
  const [selectedData, setSelectedData] = useState(null);

  const handleWidgetClick = (data) => {
    setSelectedData(data);
    setModalShow(true);
  };

  // Filtering logic based on selectedAge and selectedSex

  return (
    <div>
      <Row>
        {serverData.map((item) => (
          <Widget key={item.id} data={item} onClick={handleWidgetClick} />
        ))}
      </Row>

      {selectedData && (
        <MyVerticallyCenteredModal
          show={modalShow}
          onHide={() => setModalShow(false)}
          data={selectedData}
        />
      )}
    </div>
  );
};

function ParSearch() {
  const [serverData, setServerData] = useState([]);
  const [selectedAge, setSelectedAge] = useState('All Ages');
  const [selectedSex, setSelectedSex] = useState('All');

  const handleAgeSelect = (eventKey) => {
    setSelectedAge(eventKey);
  };

  const handleSexSelect = (eventKey) => {
    setSelectedSex(eventKey);
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    try {
      const response = await axios.post('http://127.0.0.1:5000/participant-search', {
        age: selectedAge,
        sex: selectedSex,
      });

      // Handle the response data (filtered trials) here, e.g., set the serverData state
      setServerData(response.data);
      console.log(response.data)
    } catch (error) {
      console.error('Error fetching trials:', error);
    }
  };

  useEffect(() => {
    const fetchInitialData = async () => {
      try {
        const initialDataResponse = await axios.get('http://127.0.0.1:5000/initial-data'); // Change the URL to your actual endpoint
        setServerData(initialDataResponse.data);
        console.log(initialDataResponse.data);
      } catch (error) {
        console.error('Error fetching initial data:', error);
      }
    };

    fetchInitialData();
